          <div className={styles.title}>{question.title}</div>
          <div
            className={styles.button}
            onClick={() => questionButtonHandler(question)}
          >
            <MdKeyboardArrowUp size={"2rem"} />
          </div>
        </div>
        {question.active && <div className={styles.text}>{question.text}</div>}
      </div>
    </div>
  );
};
const FAQ = () => {
